mapper.select:
select * from orders


        and number = #{number}


        and phone = #{phone}


        and status = #{status}


        and begin_time = #{beginTime}


        and end_time = #{endTime}



        and user_id = #{userId}


ORDER BY order_time DESC

mapper.update:
UPDATE orders
number = #{number}, status = #{status}, user_id = #{userId}, address_book_id = #{addressBookId}, order_time = #{orderTime}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, amount = #{amount}, remark = #{remark}, user_name = #{userName}, phone = #{phone}, address = #{address}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, pack_amount = #{packAmount}, tableware_number = #{tablewareNumber}, tableware_status = #{tablewareStatus},
and id = #{id} and number = #{number}

mapper.select:
select count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

select sum(amount) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}
